          <div className="card">Think and Grow Rich</div>
        </Link>
        <Link to={`/book`}>
          <div className="card">Think and Grow Rich</div>
        </Link>
        <Link to={`/book`}>
          <div className="card">Think and Grow Rich</div>
        </Link>
        <Link to={`/book`}>
          <div className="card">Think and Grow Rich</div>
        </Link>
      </div>

      {showModal && (
        <CreateBookModal
          onCancel={() => setShowModal(false)}
          onCreate={refreshPage}
        />
      )}
    </Fragment>
  )
}

Books.propTypes = {
    books: PropTypes.array.isRequired,
}

export default connect(
    mapStateToProps,
    { addBook }
)(Books)
