    <div className="note-add">
      {optionsToggle && (
        <div className="options">
          <a href="#">insight</a>
          <a href="#">question</a>
          <a href="#">quote</a>
        </div>
      )}
      <span className="note-add-icon">+</span>
    </div>
  )
}

export default AddButton
