import {
    GET_POSTS,
    UPDATE_LIKES,
    DELETE_POST,
    ADD_POST,
    POST_ERROR,
} from '../actions/types'

const initialState = {
    posts: [],
    post: null,
    loading: true,
    error: {} // FIXME: why is error an empty object?
}

export default function postReducer(state = initialState, action) {
    const { type, payload } = action

    switch (type) {

        case GET_POSTS:
            return {
                ...state,
                posts: payload,
                loading: false,
            }

        case DELETE_POST:
            return {
                ...state,
                posts: state.posts.filter(post => post._id !== payload),
                loading: false
            }

        case ADD_POST:
            return {
                ...state,
                posts: [payload, ...state.posts],
                loading: false
            }

        case UPDATE_LIKES:
            return {
                ...state,
                posts: state.posts.map(post => post._id === payload._id ? { ...post, likes: payload.likes} : post),
                loading: false,
            }

        case POST_ERROR:
            return {
                ...state,
                loading: false,
                error: payload
            }

        default:
            return state

    }
}
