import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { motion } from 'framer-motion'
import './CreateNoteModal.css'

// redux
import { addNote } from '../../actions/book'
import { connect } from 'react-redux'

const CreateNoteModal = ({ onCancel, onCreate, addNote }) => {
  const [title, setTitle] = useState('')
  const [text, setText] = useState('')
  const [characterCount, setCharacterCount] = useState(0)

  const onChange = (e) => {
    setText(e.target.value)
    setCharacterCount(e.target.value.length)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    // addNote({ title })
    onCreate()
    setTitle('')
    setText('')
  }

  return (
    <motion.div
      animate={{
        opacity: [0, 1],
      }}
      transition={{
        duration: 0.5,
      }}
    >
      <div className="post modal-center">
        <form className="post-form" onSubmit={(e) => onSubmit(e)}>
          <textarea
            cols="79"
            rows="4"
            placeholder="what's your nugget?"
            name="text"
            //   value={text}
            onChange={(e) => onChange(e)}
            maxLength="500"
            required
          ></textarea>
          <p className="character-count">{characterCount}/500</p>

          <div className="book-form">
            {/* add button */}
            <input type="submit" value="add" className="book-btn btn-success" />
            {/* cancel button */}
            <button
              type="button"
              onClick={onCancel}
              className="book-btn btn-danger"
            >
              cancel
            </button>
          </div>
        </form>
      </div>

      {/* radio buttons */}
      <div class="options">
        <div class="radio">
          <input id="radio-1" name="radio" type="radio" value="insight" />
          <label for="radio-1" class="radio-label">
            insight
          </label>
        </div>

        <div class="radio">
          <input id="radio-2" name="radio" type="radio" value="quote" />
          <label for="radio-2" class="radio-label">
            quote
          </label>
        </div>

        <div class="radio">
          <input id="radio-3" name="radio" type="radio" value="question" />
          <label for="radio-3" class="radio-label">
            question
          </label>
        </div>
      </div>
    </motion.div>
  )
}

CreateNoteModal.propTypes = {
  onCancel: PropTypes.func.isRequired,
  onCreate: PropTypes.func.isRequired,
  addNote: PropTypes.func.isRequired,
}

export default connect(null, { addNote })(CreateNoteModal)
