import React, { useState } from 'react'
import PropTypes from 'prop-types'

// redux
import { addNote } from '../../actions/book'
import { connect } from 'react-redux'

const CreateNoteModal = (props) => {
  const [title, setTitle] = useState('')

  const onSubmit = (e) => {
    e.preventDefault()
    addBook({ title })
    onCreate()
    setTitle('')
  }

  return (
    <motion.div
      animate={{
        opacity: [0, 1],
      }}
      transition={{
        duration: 0.5,
      }}
    >
      <form className="book-form" onSubmit={(e) => onSubmit(e)}>
        <label>what book are you reading?</label>
        <div className="form-group">
          <input
            type="text"
            onChange={(e) => setTitle(e.target.value)}
            placeholder="book title"
            required
          />
        </div>

        <input type="submit" value="add" className="book-btn btn-success" />
        <button
          type="button"
          onClick={onCancel}
          className="book-btn btn-danger"
        >
          cancel
        </button>
      </form>
    </motion.div>
  )
}

CreateNoteModal.propTypes = {
  onCancel: PropTypes.func.isRequired,
  onCreate: PropTypes.func.isRequired,
  addNote: PropTypes.func.isRequired,
}

export default connect(null, { addNote })(CreateNoteModal)
