import React, { Fragment, useEffect } from 'react'
import PropTypes from 'prop-types'
import Spinner from '../layout/Spinner'

// redux
import { getBook } from '../../actions/book'
import { connect } from 'react-redux'

const Notes = ({ getBook, book: { book, loading }, match }) => {
  useEffect(() => {
    console.log(match.params.book_id)
    getBook(match.params.book_id)
  }, [getBook])

  return loading || post === null ? (
    <Spinner />
  ) : (
    <Fragment>{book.notes.map(<div>this is a book note</div>)}</Fragment>
  )
}

Notes.propTypes = {
  getBook: PropTypes.func.isRequired,
  book: PropTypes.object.isRequired,
}

const mapStateToProps = (state) => ({
  book: state.book,
})

export default connect(mapStateToProps, { getBook })(Notes)
