import axios from 'axios'
import config from '../config'
import {
    GET_BOOKS,
    GET_BOOK,
    BOOK_ERROR,
    ADD_BOOK,
    DELETE_BOOK,
    ADD_NOTE,
    DELETE_NOTE,
} from './types'

// book
export const getBooks = () => async (dispatch) => {
    try {

        const res = await axios.get(`${config.apiUrl}/books`)

        dispatch({
            type: GET_BOOKS,
            payload: res.data
        })

    } catch (err) {
        dispatch({
            type: BOOK_ERROR,
            payload: { msg: err.response.statusText, status: err.response.status }
        })
    }
}

export const getBook = book_id => async (dispatch) => {
    try {

        const res = await axios.post(`${config.apiUrl}/books/${book_id}`)

        dispatch({
            type: GET_BOOK,
            paylaod: res.data
        })

    } catch (err) {
        dispatch({
            type: BOOK_ERROR,
            payload: { msg: err.response.statusText, status: err.response.status }
        })
    }
}

export const deleteBook = book_id => async (dispatch) => {
    try {

        await axios.delete(`${config.apiUrl}/books/${book_id}`)

        dispatch({
            type: DELETE_BOOK,
            payload: book_id
        })

    } catch (err) {
        dispatch({
            type: BOOK_ERROR,
            payload: { msg: err.response.statusText, status: err.response.status}
        })
    }
}

export const addBook = formData => async (dispatch) => {
    try {

        const res = await axios.post(`${config.apiUrl}/books`, formData, config.apiConfig)

        dispatch({
            type: ADD_BOOK,
            payload: res.data
        })

    } catch (err) {
        dispatch({
            type: BOOK_ERROR,
            payload: { msg: err.response.statusText, status: err.response.status}
        })
    }

}

// note
