import React, { useState } from 'react'
import { motion } from 'framer-motion'
import PropTypes from 'prop-types'
import './CreateBookModal.css'

const CreateBookModal = ({ onCancel, onCreate }) => {
  const [book, setBook] = useState('')

  const onSubmit = (e) => {
    console.log('form submitted')
    e.preventDefault()
  }
  return (
    <motion.div
      animate={{
        opacity: [0, 1],
      }}
      transition={{
        duration: 0.5,
      }}
    >
      <form className="book-form" onSubmit={(e) => onSubmit(e)}>
        <label>what book are you reading?</label>
        <div className="form-group">
          <input
            type="text"
            onChange={(e) => setBook(e.target.value)}
            placeholder="book title"
            required
          />
        </div>

        <input type="submit" value="add" className="book-btn"/>
        <button type="button" onClick={onCancel} className="book-btn">
          Cancel
        </button>
      </form>
    </motion.div>
  )
}

CreateBookModal.propTypes = {
  onCancel: PropTypes.func.isRequired,
  onCreate: PropTypes.func.isRequired,
}

export default CreateBookModal
