import React, { Fragment, useState } from 'react'
import PropTypes from 'prop-types'
import './PostItem.css'

// font awesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart as fasHeart } from '@fortawesome/free-solid-svg-icons'
import { faComment, faHeart as farHeart } from '@fortawesome/free-regular-svg-icons'

// redux
import { connect } from 'react-redux'

// components
import Moment from 'react-moment'

const PostItem = ({ post, showActions }) => {
    const {
        _id,
        text,
        name,
        avatar,
        likes,
        comments,
        date
    } = post

    const [likeToggle, setLikeToggle] = useState(false)

    const changeLike = () => {
        console.log(likeToggle)
        setLikeToggle(!likeToggle)
        // addLike(_id)
    }

    return (
        <div className="post">
            <div className="post-info">
                <div>
                    <img className="post-img" src={avatar} alt=""/>
                </div>
                <div>
                    <p className="post-user">{name}</p>
                    <p className="post-date">
                        Posted on <Moment format="YYYY/MM/DD">{date}</Moment>
                    </p>
                </div>
            </div>
            <div>
                <p>{text}</p>
                <button className="post-actions">
                    <FontAwesomeIcon icon={faComment} />
                    {' '}
                    <span>25</span>
                </button>
                <button className="post-actions"
                    onClick={e => changeLike()}>
                    {likeToggle ? <FontAwesomeIcon icon={fasHeart} /> : <FontAwesomeIcon icon={farHeart} />}
                    {' '}
                    <span>120</span>
                </button>
            </div>
        </div>
    )
}

PostItem.defaultProps = {
    showActions: true
}

PostItem.propTypes = {
    post: PropTypes.object.isRequired,
}

export default PostItem
