import React, { Fragment, useState, useEffect } from 'react'
import PropTypes from 'prop-types'

// components
import { Link } from 'react-router-dom'
import CreateBookModal from './CreateBookModal'

// redux
import { connect } from 'react-redux'
import { addBook, getBooks } from '../../actions/book'

const Books = ({ book, addBook, getBooks }) => {
  const { books, loading } = book
    const [showModal, setShowModal] = useState(false)

    useEffect(() => {
        getBooks()
    }, [getBooks])

  const refreshPage = () => {
    setShowModal(false)
    getBooks()
  }

  return (
    <Fragment>
      <div className={showModal ? 'cards blur' : 'cards'}>
        <div className="card card-add" onClick={() => setShowModal(true)}>
          <span className="plus-icon">+</span>
        </div>
              {books.map(book => <Book key={book._id} book={book}/>)
        }
      </div>

      {showModal && (
        <CreateBookModal
          onCancel={() => setShowModal(false)}
          onCreate={refreshPage}
        />
      )}
    </Fragment>
  )
}

Books.propTypes = {
  book: PropTypes.object.isRequired,
  addBook: PropTypes.func.isRequired,
  getBooks: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => ({
  book: state.book,
})

export default connect(mapStateToProps, { addBook, getBooks })(Books)
