import React, { Fragment, useState } from 'react'
import PropTypes from 'prop-types'
import './auth.css'

const Login = props => {
    const [formData, setFormData] = useState({
        email: "",
        password: "",
    })

    const {
        email,
        password
    } = formData

    const onChange = e => {
        setFormData({ ...formData, [e.target.name]: e.target.value })
    }

    const onSubmit = e => {
        e.preventDefault()
        console.log(formData)
        // call Login action
    }

    return (
        <Fragment>
            <form className="form" onSubmit={e => onSubmit(e)}>
                <h2>welcome back</h2>
                <div className="form-group">
                    <input
                        type="email"
                        placeholder="email"
                        name="email"
                        value={email}
                        onChange={e => onChange(e)}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="password"
                        minLength="6"
                        name="password"
                        value={password}
                        onChange={e => onChange(e)}
                        required
                    />
                </div>
                <input
                    type="submit"
                    className="btn"
                    value="log in"
                />
            </form>
        </Fragment>
    )
}

Login.propTypes = {

}

export default Login
